const machineToHumanObj = {
  1: "OPERATIONAL",
  2: "PERFORMANCE ISSUES",
  4: "MAJOR OUTAGE",
};

const humanToMachineObj = {
  OPERATIONAL: 1,
  "PERFORMANCE ISSUES": 2,
  "MAJOR OUTAGE": 4,
};

function componentMachineToHuman(machine) {
  return machineToHumanObj[machine];
}

function componentHumanToMachine(human) {
  return humanToMachineObj[human];
}

module.exports = { componentHumanToMachine, componentMachineToHuman };
