const { getDate } = require("./date-utils");

function normalLog(str) {
  console.log(`[INFO - ${getDate()}] ${str}`);
}

function errorLog(str, err) {
  console.trace(`[ERROR - ${getDate()}] ${str}${err ? "\n->" + JSON.stringify(err) : ""}`);
}

//When you want a log to be easily identified
function specialLog(str) {
  console.log(`[-----------------------------------------------------]`);
  console.log(`[IMPORTANT - ${getDate()}] ${str}`);
  console.log(`[-----------------------------------------------------]`);
}

function databaseLog(str) {
  console.log(`[DATABASE ACTION - ${getDate()}] ${str}`);
}

function warningLog(str) {
  console.warn(`[WARNING - ${getDate()}] ${str}`);
}

function httpLog(type, url, code, response) {
  console.log(
    `[HTTP (${type.toUpperCase()}) - ${getDate()}]\n=>URL: ${url}\n=>CODE: ${code}\n=>RESPONSE: ${
      JSON.stringify(response) || ""
    }`
  );
}

module.exports = { databaseLog, specialLog, normalLog, errorLog, warningLog, httpLog };
