const { errorLog } = require("./format-log");

/**
 * Get a message from Discord (only works for Discord API)
 * @param {object} discordClient - Client from the discord.js library
 * @param {string} channelId - Channel id where it will look for the message
 * @param {string} messageId  - Message id of the message to get
 */
module.exports = async (discordClient, channelId, messageId) => {
  const message = await discordClient.channels.cache
    .get(channelId)
    .messages.fetch(messageId)
    .catch((err) => errorLog(`Error trying to get a message from Discord`, err));

  return message || undefined;
};
