const machineToHumanObj = {
  1: "INVESTIGATING",
  2: "IDENTIFIED",
  3: "WATCHING",
  4: "FIXED",
};

const humanToMachineObj = {
  INVESTIGATING: 1,
  IDENTIFIED: 2,
  WATCHING: 3,
  FIXED: 4,
};

function incidentMachineToHuman(machine) {
  return machineToHumanObj[machine];
}

function incidentHumanToMachine(human) {
  return humanToMachineObj[human];
}

module.exports = { incidentHumanToMachine, incidentMachineToHuman };
