/**
 * Generate a random id, either letters or numbers
 * @param {string} type - The type of random id [ num, id ]
 */
module.exports = (type) => {
  return type == "num" ? randomNum() : randomID();
};

function randomID() {
  return `${Math.random().toString(36).substr(2, 9)}&${Math.random().toString(36).substr(2, 9)}`;
}

function randomNum(max = 999999999) {
  return Math.round(Math.random() * max);
}

