/**
 * Remove the duplicates object based on a specific key
 * @param {object[]} list - List to remove duplicates from
 * @param {string} key - Key to determine which elements to remove
 */
module.exports = function (list, key) {
  const cachedList = [];
  const returnList = [];

  for (const element of list) {
    if (!cachedList.includes(element[key])) {
      cachedList.push(element[key]);
      returnList.push(element);
    }
  }

  return returnList;
};
